(58, '2017-05-30 01:46:20', '2017-11-13 06:32:55', NULL, '1', 'RESIDENT CONSULTANT ', NULL, '<p style="text-align:justify"><span style="font-family:&quot;Arial&quot;,sans-serif">KPJ Sentosa KL Specialist Hospital is a multidisciplinary tertiary care hospital with more than 45 years of service excellence.&nbsp;&nbsp; In view of our business expansion, we urgently seek highly motivated, qualified and experienced professionals who share our passion in the provision of quality and excellent healthcare, as part of our growing team.</span></p><h5>Job Description:</h5><ul><li><span style="font-family:&quot;Arial&quot;,sans-serif">General Surgeon&nbsp; </span></li><li><span style="font-family:&quot;Arial&quot;,sans-serif">General / Internal Medicine Physician &nbsp;</span></li><li><span style="font-family:&quot;Arial&quot;,sans-serif">Obstetrician &amp; Gynaecologist &nbsp;</span></li><li><span style="font-family:&quot;Arial&quot;,sans-serif">Ophthalmologist</span></li><li><span style="font-family:&quot;Arial&quot;,sans-serif">Paediatrician</span></li></ul>', '<h5>Job Requirements:</h5><ul><li><span style="font-family:&quot;Arial&quot;,sans-serif">Preferably with sub speciality &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></li><li><span style="font-family:&quot;Arial&quot;,sans-serif">Minimum 3-5 years Post Graduate experience </span></li><li><span style="font-family:&quot;Arial&quot;,sans-serif">Registered with the Malaysian Medical Council and National Specialist Register</span></li><li><span style="font-family:&quot;Arial&quot;,sans-serif">Possess a valid annual practising certificate and NSR </span></li></ul>', '<p><strong><span style="font-family:&quot;Arial&quot;,sans-serif; font-size:11.0pt">Please email your resume to </span></strong><span style="font-family:&quot;Calibri&quot;,sans-serif; font-size:11.0pt"><a data-cke-saved-href="mailto:hettidayana@kpjsentosa.com" href="mailto:hettidayana@kpjsentosa.com"><strong><span style="font-family:&quot;Arial&quot;,sans-serif">kskl-tms@kpjsentosa.com</span></strong></a></span><strong><span style="font-family:&quot;Arial&quot;,sans-serif; font-size:11.0pt"> with latest photograph and complete with copies of certificates by 15<sup>th</sup> June 2017.&nbsp; Only shortlisted candidates will be contacted</span></strong><br></p>', NULL, NULL),
(59, '2017-05-30 01:55:34', '2017-11-13 06:33:13', NULL, '1', 'RESIDENT MEDICAL OFFICER', NULL, '<p style="text-align:justify"><span style="font-family:&quot;Arial&quot;,sans-serif">KPJ Sentosa KL Specialist Hospital is a multidisciplinary tertiary care hospital with more than 45 years of service excellence.&nbsp;&nbsp; In view of our business expansion, we urgently seek highly motivated, qualified and experienced professionals who share our passion in the provision of quality and excellent healthcare, as part of our growing team.</span></p>', '<h5>Job Requirements:</h5><p><span style="font-family:&quot;Arial&quot;,sans-serif"> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; MBBS or equivalent from recognized institutions<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Registered with Malaysian Medical Council<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Possess valid annual practicing certificate<br> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Willing to work shifts</span></p><p><br></p>', '<p style="text-align:justify"><strong><span style="font-family:&quot;Arial&quot;,sans-serif">Please email your resume to </span></strong><a data-cke-saved-href="mailto:hettidayana@kpjsentosa.com" href="mailto:hettidayana@kpjsentosa.com"><strong><span style="font-family:&quot;Arial&quot;,sans-serif">kskl-tms@kpjsentosa.com</span></strong></a><strong><span style="font-family:&quot;Arial&quot;,sans-serif"> with latest photograph and complete with copies of certificates by 15<sup>th</sup> June 2017.&nbsp; Only shortlisted candidates will be contacted.</span></strong><br></p>', NULL, NULL),
